import React, { useEffect, useState } from "react"
import {Container} from "react-bootstrap";
import "../styles/CreateMemePage.css";

export default function CreateMemePage() {

    const [templates, setTemplates] = useState([]);

    useEffect(() => {
        fetch("https://api.imgflip.com/get_memes")
        .then(x => x.json().then(res => setTemplates(res.data.memes))
        );
    }, []);

    return (
        <div>
            <h1>Create a Meme</h1>
            <Container fluid className="meme-display-container">
                {templates.map(images => {
                    return (
                        <img className="meme-img ml-2 mt-2" key={images.id} src={images.url} alt={images.name}/>
                    )
                })}
            </Container>
        </div>
    );
}
