import React, { useRef, useState } from "react";
import { Form, Button, Card, Container, Alert  } from "react-bootstrap";
import { useAuth } from "../contexts/AuthContext";
import {Link, useHistory} from "react-router-dom";
import Navbar from "../components/Navbar";

export default function UpdateProfile() {
    const emailRef = useRef();
    const passwordRef = useRef();
    const passwordConfRef = useRef();
    const { currentUser } = useAuth();
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const history = useHistory();
  
    async function handleSubmit(event) {
      event.preventDefault();
  
      //Password confirmation to check if the password is == to the re-entered password
      if(passwordRef.current.value !== passwordConfRef.current.value) {
        return setError("Passwords do not match");
      }
  
      try {
        setError("");
        setLoading(true)
        // await signup(emailRef.current.value, passwordRef.current.value);
        history.push("/profile");
      } catch {
        setError("Failed to create an account");
      }
      setLoading(false)
    }
  
    return (
        <>
            <Navbar />
            <Container className="d-flex align-items-center justify-content-center" style={{minHeight: "100vh"}}>
                <div className="w-100" style={{maxWidth: "400px"}}>
                    <Card>
                        <Card.Body>
                        <h2 className="text-center md-4">Update Profile</h2>
                        {error && <Alert variant="danger">{error}</Alert>}
                        <Form onSubmit={handleSubmit}>
                            <Form.Group id="email">
                                <Form.Label>Email</Form.Label>
                                <Form.Control type="email" ref={emailRef} required defaultValue={currentUser.email}/>
                            </Form.Group>
                            <Form.Group id="password">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" ref={passwordRef} required placeholder="Leave blank to keep the same"/>
                            </Form.Group>
                            <Form.Group id="password-confirm">
                                <Form.Label>Re-Enter Password</Form.Label>
                                <Form.Control type="password" ref={passwordConfRef} required placeholder="Leave blank to keep the same"/>
                            </Form.Group>
                            <Button disabled={loading} className="w-100" type="submit">Update</Button>
                        </Form>
                        </Card.Body>
                    </Card>
                    <div className="w-100 text-center mt-2">
                        <Link to="/profile">Cancle</Link>
                    </div>
                </div>
            </Container>
        </>
    );
  }
